import React, { useState, useEffect, useCallback } from "react";

import MoviesList from "./components/MoviesList";
import AddMovie from "./components/AddMovie";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchMoviesHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(
        "https://react-http-60722-default-rtdb.firebaseio.com/movies.json"
      );
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const loadedMovies = [];

      const data = await response.json(); // data is now a object of objects

      for (const key in data) {
        loadedMovies.push({
          id: key,
          title: data[key].title,
          openingText: data[key].openingText,
          release: data[key].releaseDate,
        });
      }

      setMovies(loadedMovies);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchMoviesHandler();
  }, [fetchMoviesHandler]);

  async function addMovieHandler(movie) {
    const response = await fetch(
      "https://react-http-60722-default-rtdb.firebaseio.com/movies.json", // movies is up to me
      {
        method: "POST", // GET by default
        body: JSON.stringify(movie), // movies object to json format
        headers: {
          "Content-type": "application/type",
        },
      }
    );
    const data = await response.json();
    // console.log(data);
  }

  async function deleteMovieHandler(movie) {
    const response = await fetch(
      "https://react-http-60722-default-rtdb.firebaseio.com/movies.json",
      {
        method: "DELETE",
        body: JSON.stringify(movie),
        headers: {
          "Content-type": "application/type",
        },
      }
    );
    const data = await response.json();
    console.log(data);
  }

  let content = <p>Found no movies.</p>;

  if (movies.length > 0) {
    content = <MoviesList onDeleteMovie={deleteMovieHandler} movies={movies} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <React.Fragment>
      <section>
        <AddMovie
          onAddMovie={addMovieHandler}
          onDeleteMovie={deleteMovieHandler}
        />
      </section>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>{content}</section>
    </React.Fragment>
  );
}

export default App;
